mapper.select:
SELECT s.*, i.*, e.heating_subsidy, e.name employee_name, j.id job_id, j.name job_name, d.id dept_id, d.name dept_name FROM salary s, employee e, job j, dept d, import i WHERE s.employee_id = e.id AND s.import_id = i.id AND e.job_id = j.id AND e.status = 1 AND e.dept_id = d.id
AND d.id = #{deptId}
AND DATE_FORMAT(s.create_time, '%Y-%m') = #{time}
AND d.id = #{deptId} AND DATE_FORMAT(s.create_time, '%Y-%m') = #{time}

SELECT s.*, i.*, e.heating_subsidy, e.name employee_name, j.id job_id, j.name job_name, d.id dept_id, d.name dept_name FROM salary s, employee e, job j, dept d, import i WHERE s.employee_id = e.id AND s.import_id = i.id AND e.job_id = j.id AND e.status = 1 AND e.dept_id = d.id
AND DATE_FORMAT(s.create_time, '%Y-%m') >= #{beginDate} AND DATE_FORMAT(s.create_time, '%Y-%m') <= #{endDate} AND d.name LIKE CONCAT('%', #{deptName}, '%')
AND DATE_FORMAT(s.create_time, '%Y-%m') = #{beginDate} AND d.name LIKE CONCAT('%', #{deptName}, '%')
AND DATE_FORMAT(s.create_time, '%Y-%m') >= #{beginDate} AND DATE_FORMAT(s.create_time, '%Y-%m') <= #{endDate}
AND DATE_FORMAT(s.create_time, '%Y-%m') = #{beginDate}
AND d.name LIKE CONCAT('%', #{deptName}, '%')
